<?php

namespace ad\ClassifiedBundle\Entity\Repository;

use Gedmo\Tree\Entity\Repository\NestedTreeRepository;
use ad\ClassifiedBundle\Entity\Category;
use Doctrine\ORM\EntityRepository;

/**
 * CategoryRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class CategoryRepository extends NestedTreeRepository
{
	public function findAllSubCat()
	{
		$em = $this->getEntityManager();
		
		$qb = $em->createQueryBuilder();
		$qb->addSelect('c');
		$qb->from('adClassifiedBundle:Category','c');
		$qb->where('c.parent is not null');
		
		return $response = $qb->getQuery()->getResult();
	}

	public function findCatBySlug($slug)
	{
		$em = $this->getEntityManager();
	
		$qb = $em->createQueryBuilder();
		$qb->addSelect('c');
		$qb->from('adClassifiedBundle:Category','c');
		$qb->where('c.slug = :slug');
		
		$qb->setParameter('slug', $slug);
	
		$response = $qb->getQuery()->getResult();
		
		return $response[0];
	}
}
